import NavBar from "./components/navbar"
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction'; // For clickable events
import axios from 'axios';
import { useEffect, useState } from 'react';
import { formatISO } from 'date-fns';

export default function Home() {
    const [events, setEvents] = useState([]);
    const [date, setDates] = useState([])
    const [calendar, setCalendar] = useState([]);

    useEffect(() => {
      const fetchEvents = async () => {
        try {

          const res = await axios.get('http://localhost:3001/fetch-calendar-events', { withCredentials: true });
          // Transform events to the format FullCalendar expects
          const transformedEvents = res.data.map(event => {
            const start = event.start?.dateTime || event.start?.date;
            const end = event.end?.dateTime || event.end?.date;
            const allDay = Boolean(event.start?.date);
            // Ensure that both `start` and `end` exist, and handle all-day events properl
            return {
                ...event, 
                start: start, 
                end: end,
                allDay
            };
        });

        const transformedEventsCalendar = res.data.map(event => {
          const start = event.start?.dateTime || event.start?.date;
          const end = event.end?.dateTime || event.end?.date;
          const allDay = Boolean(event.start?.date);
          // Ensure that both `start` and `end` exist, and handle all-day events properl
          return {
            id: event.id, // ensure this is unique for each event
            title: event.summary, // FullCalendar uses 'title', not 'summary'
            start: start, 
            end: end, // Use ISO string or Date object
          };
      });
          setEvents(transformedEvents);
          setCalendar(transformedEventsCalendar)
          console.log(transformedEvents)
        } catch (error) {
          console.error('Error fetching calendar events:', error);
        }
      };
      fetchEvents();
    }, []);

    return (
        <div>

            <h1>My Calendar Events</h1>
            <FullCalendar
      plugins={[dayGridPlugin, interactionPlugin]}
      initialView="dayGridMonth"
      events={events}
      eventClick={(clickInfo) => {
        // Handle event click
        alert(clickInfo.event.summary);
      }}
    />
    <button onClick={() => console.log(events)}>halo</button>
    <ul>
        {events.map(event => (
          <li key={event.id}>
            <div className="my-5">
              <div className="flex flex-col">
                <text> {event.summary}</text>
                <text> {event.start}</text>
                <text> {event.end}</text>
              </div>
            </div>

          </li>
        ))}
      </ul>

    </div>
    )
}
